import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function ItemManager() {
    document.title = 'Stock Manager'

    const navigateTo = useNavigate();

    const [formData, setFormData] = useState({
        id: Math.floor(1000 + (Math.random() * 9000)),
        imgURL: '',
        name: '',
        type: '',
        quantity: 0,
        supplier: '',
        note: ''
    });

    // Stock Register function start
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        })
    };

    const handleSubmit = (e) => {
        e.preventDefault()

        fetch('http://localhost:3000/materials/', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(formData)
          })
          .then(r => r.json())
          .then(() => navigateTo('/'))
          .catch((err) => alert(err.message))
    }
    // Stock Register function end



    return (
        <div className='display-component'>
            <h2>Stock Manager</h2>


            <form className='item-register' onSubmit={handleSubmit}>
                <label>Stock Name</label>
                <input type='text' name='name' onChange={handleChange} placeholder='ex: wood plank' />

                <label>Stock Type</label>
                <input type='text' name='type' onChange={handleChange} placeholder='ex: wood' />

                <label>Quantity</label>
                <input type='number' name='quantity' min={1} max={100} onChange={handleChange} />

                <label>Supplier</label>
                <input type='text' name='supplier' onChange={handleChange} placeholder='ex: green company' />

                <label>Image URL</label>
                <input type='url' name='imgURL' onChange={handleChange} placeholder='enter your image url' />

                <label>Add Note</label>
                <input type='text' name='note' onChange={handleChange} />

                <input type='submit' value='Register' />
            </form>
        </div>
    );
};
