import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function ItemManager() {
    document.title = 'Stock Manager'

    const navigateTo = useNavigate();
    const [stock, setStock] = useState([]);

    const [formData, setFormData] = useState({
        id: Math.floor(1000 + (Math.random() * 9000)),
        imgURL: '',
        name: '',
        type: '',
        quantity: 0,
        supplier: '',
        note: ''
    });

    const registerForm = () => {
        return (
            <form className='stock-manager-form' onSubmit={handleRegisterSubmit}>
                <h3>Register New Stock</h3>

                <input type='text' name='name' onChange={handleRegisterChange} placeholder='Stock Name' />
                <input type='text' name='type' onChange={handleRegisterChange} placeholder='Type' />
                <input type='number' name='quantity' min={1} max={100} onChange={handleRegisterChange} placeholder='Quantity'/>
                <input type='text' name='supplier' onChange={handleRegisterChange} placeholder='Supplier' />
                <input type='url' name='imgURL' onChange={handleRegisterChange} placeholder='Image URL' />
                <input type='text' name='note' onChange={handleRegisterChange} placeholder='Add note' />

                <input type='submit' value='Register' />
            </form>
        );
    }

    const editorForm = () => {
        return (
            <form className='stock-manager-form' onSubmit={handleEditSubmit}>
                <h3>Edit Stock</h3>

                {stock ? <div className='edit-stock-preview'>
                            <p><b>Selected Stock</b></p>
                            <img src={stock.imgURL} alt={stock.name} width={'50px'} />
                            <p>{stock.name}</p>
                            <p>{stock.type}</p>
                            <p>{stock.quantity}</p>
                            <p>{stock.supplier}</p>
                        </div> : null}

                <input type='text' name='id' onChange={handleEditIDchange} required={true} maxLength={4} placeholder='Stock#' />
                <input type='text' name='name' onChange={handleEditChange} placeholder='New name' />
                <input type='text' name='type' onChange={handleEditChange} placeholder='New stock type' />
                <input type='number' name='quantity' min={1} max={100} onChange={handleEditChange} placeholder='New quantity' />
                <input type='text' name='supplier' onChange={handleEditChange} placeholder='New supplier' />
                <input type='url' name='imgURL' onChange={handleEditChange} placeholder='New image URL' />
                <input type='text' name='note' onChange={handleEditChange} placeholder='New note' />
                <input type='submit' value='Update' />
            </form>
        );
    }

    const deleteForm = () => {
        return (
            <form className='stock-manager-form' onSubmit={handleDeleteSubmit}>
                <h3>Delete Stock</h3>

                <input type='text' name='id' minLength={4} maxLength={4} placeholder='Stock#'></input>
                <input type='submit' value='Delete'></input>
            </form>
        );
    }


    const handleRegisterChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        })
    };


    const handleRegisterSubmit = (e) => {
        e.preventDefault()

        fetch('http://localhost:3000/materials/', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(formData)
          })
          .then(r => r.json())
          .then(() => navigateTo('/'))
          .catch((err) => alert(err.message))
    };


    const handleEditIDchange = (e) => {
        fetch(`http://localhost:3000/materials/${+e.target.value}`)
        .then(r => r.json())
        .then(setStock)
        .catch(err => alert(err.message))
    }   


    const handleEditChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        })
    };

    const handleEditSubmit = (e) => {
        e.preventDefault();

        fetch(`http://localhost:3000/materials/${formData.id}`, {
            method: 'PATCH',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(formData)
          })
          .then(r => r.json())
          .then(() => navigateTo('/'))
          .catch((err) => alert(err.message))
    };

    const handleDeleteSubmit = (e) => {
        e.preventDefault();
    };

    const displayForm = (e) => {
        e
    }

    return (
        <div className='display-component'>
            <h2>Stock Manager</h2>

            {/* <div className='stock-manager-form-container'> */}
            <div>
                <label>I want to </label>
                <select onChange={displayForm}>
                    <option>Please elect</option>
                    <option>Register new stock</option>
                    <option>Edit stock</option>
                    <option>Delete stock</option>
                </select>
                {/* {registerForm()} */}
                {/* {editorForm()} */}
                {/* {deleteForm()} */}
            </div>
        </div>
    );
};
