import React, { useState, useEffect } from 'react';
import NavBar from './NavBar';
import Dashboard from './Dashboard';

export default function App() {
  const [stocks, setStocks] = useState([]);
  const [newQuantity, setNewQuantity] = useState(0);

  useEffect(() => {
    fetch("http://localhost:3000/materials")
    .then(r => r.json())
    .then(setStocks)
    .catch((err) => alert(err.message))
  }, [])

  const handleQuantityChange = (e) => {
    setNewQuantity(+e.target.value)
  };

  const handleQuantitySubmit = (e, targetStock) => {
    e.preventDefault();

    const newStocks = stocks.map((stock) => {
      if (stock.id === targetStock.id) {
        return {
          ...stock, 
          quantity: newQuantity
        }
      } else {
        return stock;
      }
    })

    setStocks(newStocks);

    fetch("http://localhost:3000/materials/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({newStocks})
    })
  }

  return (
    <div>
      <h1 className='header'>Welcome to Inventory Manager</h1>

      <NavBar />
      <Dashboard stocks={stocks} handleQuantitySubmit={handleQuantitySubmit} handleQuantityChange={handleQuantityChange} />
    </div>
  );
}
