import { useNavigate } from 'react-router-dom';
import { handleIDChange, handleDeleteSubmit } from "./FormHandlers";

const DeleteForm = () => {
    const navigateTo = useNavigate();

    return (
        <div>
            <input type='button' value='Back to Dashboard' onClick={() => navigateTo('/')}/>

            <form className='stock-manager-form' onSubmit={handleDeleteSubmit}>
                <h3>Delete Stock</h3>
                <input type='text' name='id' onChange={handleIDChange} maxLength={4} placeholder='Stock#'></input>
                <input type='submit' value='Delete'></input>
            </form>
        </div>
    );
}

export default DeleteForm;
