import React, { useState } from 'react';

export default function ItemManager() {
    document.title = "Item Manager"

    const [formData, setFormData] = useState({
        id: Math.floor(1000 + (Math.random() * 9000)),
        imgURL: "",
        name: "",
        type: "",
        quantity: 0,
        supplier: "",
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        })
    };

    const handleSubmit = () => {
        fetch("http://localhost:3000/materials/", {
            method: "POST",
            headers: {
              "Content-Type": "application/json"
            },
            body: JSON.stringify(formData)
          })
          .then(r => r.json())
          .then(alert("New stock added."))
          .catch((err) => alert(err.message))
    }

    return (
        <div className='display-component'>
            <h2>Item Manager</h2>

            <form className='item-register' onSubmit={() => handleSubmit()}>
                <h3>Item Register</h3>

                <div>
                    <label>Name: <input type='text' name='name' onChange={handleChange} placeholder='ex: wood plank' /></label>
                    <label>Type: <input type='text' name='type' onChange={handleChange} placeholder='ex: wood' /></label>
                    <label>Quantity: <input type='number' name='quantity' min={1} max={100} onChange={handleChange} /></label>
                    <label>Supplier: <input type='text' name='supplier' onChange={handleChange} placeholder='ex: green company' /></label>
                    <label>Image URL: <input type='url' name='imgURL' onChange={handleChange} placeholder='enter your image url' /></label>
                </div>
                <input type='submit' value='Register' />
            </form>
        </div>
    );
};
