import React, { useState, useEffect } from 'react';

export default function Dashboard() {
  const [stocks, setStocks] = useState([]);
  const [newQuantity, setNewQuantity] = useState(0);
  const [filterByID, setFilterByID] = useState("");
  const [filterByType, setFilterByType] = useState('type');

  const types = [...stocks].map(stock => stock.type);
  const suppliers = [...stocks].map(stock => stock.supplier);

  document.title = "Dashboard";


  useEffect(() => {
    fetch("http://localhost:3000/materials")
    .then(r => r.json())
    .then(setStocks)
    .catch(err => alert(err.message))
  }, []);


  const handleQuantitySubmit = (e, targetStock) => { // Quantity change should be made in the database (Patch)
    e.preventDefault();

    const newStocks = [...stocks].map(stock => (stock.id === targetStock.id) ? {...stock, quantity: newQuantity} : stock); 
    setStocks(newStocks);
  }


  const filteredStocks = [...stocks].filter(stock => {if (filterByID === stock.id || filterByID === "") return true});
//   .filter(stock => {if (filterByType === 'type') return true})


    return (
        <div className="display-component">
            <div className="search-filter-bar">
                <label>Search By: </label>
                <input placeholder='Stock# (4 digits)' maxLength={4} onChange={(e) => setFilterByID(+e.target.value)} />


                <label> Filter By: </label>
                <select onChange={(e) => setFilterByType(e.target.value)}>
                    <option value='type'>Type</option>
                    {types.map((type, index) => {
                        if (index === types.indexOf(type)) return <option key={index} value={type}>{type}</option>
                    })}
                </select>


                <select onChange={null}>
                    <option value='supplier'>Supplier</option>
                    {suppliers.map((supplier, index) => {
                        if (index === suppliers.indexOf(supplier)) return <option key={index} value={supplier}>{supplier}</option>
                    })}
                </select>
            </div>


            <h3>List of Stocks</h3>

            <table>
                <thead className='table-header'>
                    <tr>
                        <td>ID</td>
                        <td>Image</td>
                        <td>Name</td>
                        <td>Type</td>
                        <td>Quantity</td>
                        <td>Supplier</td>
                        <td>In-stock</td>
                    </tr>
                </thead>
                <tbody>
                    {filteredStocks.map(stock => {
                        const { id, imgURL, name, type, quantity, supplier} = stock; 

                        return (
                            <tr key={id} className='table-stocks'>
                                <td>{id}</td>
                                <td>
                                    <img src={imgURL} alt={name} width={"50px"} onClick={null}/>
                                </td>
                                <td>{name}</td>
                                <td>{type}</td>
                                <td>
                                    Current: {quantity}
                                    <form onSubmit={(e) => handleQuantitySubmit(e, stock)}>
                                        <label>New Quantity: </label>
                                        <input type='number' name='quantity' min={0} max={100} onChange={(e) => setNewQuantity(+e.target.value)} />
                                        <input type='submit' value='Update' />
                                    </form>
                                </td>
                                <td>{supplier}</td>
                                <td>{(stock.quantity > 0) ? "Yes" : "No"}</td>
                            </tr>
                            );
                        })
                    }
                </tbody>
            </table>
        </div>
    )
};
