import React, { useState, useEffect } from 'react';
import NavBar from './NavBar';
import Dashboard from './Dashboard';

export default function App() {
  const [stocks, setStocks] = useState([]);
  const [newQuantity, setNewQuantity] = useState(0);
  const [filterByID, setFilterByID] = useState();
  const [filterByType, setFilterByType] = useState();


  useEffect(() => {
    fetch("http://localhost:3000/materials")
    .then(r => r.json())
    .then(setStocks)
    .catch(err => alert(err.message))
  }, []);


  // const handleFilterByID = (e) => {
  //   setFilterByID(+e.target.value);
  // };


  const handleFilterByType = (e) => {
    setFilterByType(e.target.value)
  }


  const handleQuantityChange = (e) => {
    setNewQuantity(+e.target.value)
  };


  const handleQuantitySubmit = (e, targetStock) => {
    e.preventDefault();

    const newStocks = stocks.map(stock => (stock.id === targetStock.id) ? {...stock, quantity: newQuantity} : stock); 
    setStocks(newStocks);
  }

  useEffect(() => {
    const handleFilterByID = (e) => {
      setFilterByID(+e.target.value);
    };

    return handleFilterByID;
  }, [filterByID])

  // const filteredByID = [...stocks].filter(stock => {if (filterByID === stock.id) return true});
  // const filteredByType = [...stocks].filter(stock => {if (filterByType === stock.type) return true});

  return (
    <div>
      <NavBar />
      <Dashboard stocks={stocks}
                 handleQuantitySubmit={handleQuantitySubmit} 
                 handleQuantityChange={handleQuantityChange} 
                 handleFilterByID={handleFilterByID}
                 handleFilterByType={handleFilterByType} />
    </div>
  );
}
