import { useState, useEffect } from 'react';

const useFetch = (url, param) => {
    const [stocks, setStocks] = useState([]);

    useEffect(() => {
        param ? 
        fetch(`${url}/${param}`)
        .then(r => r.json())
        .then(setStocks)
        .catch(err => alert(err.message)) : 

        fetch(url)
        .then(r => r.json())
        .then(setStocks)
        .catch(err => alert(err.message))
    }, [url])

    return [stocks];
}

export default useFetch;
