const HandleQuantityForm = ({ setNewQuantity, handle }) => {

    return (
        <form className='stock-row-quantity-form' onSubmit={e => props.handleQuantitySubmit(e, props.stock)}>
            <label>New Quantity: </label>

            <input type='number' name='quantity' min={0} max={100} onChange={e => props.setNewQuantity(+e.target.value)} />
            <input type='submit' value='Update' />
        </form>
    );
};

export default HandleQuantityForm;
