import { useState, useEffect } from 'react';

const Timer = () => {
    const [time, setTime] = useState(0);
    const [timerStart, setTimerStart] = useState(false);
    const [buttonValue, setButtonValue] = useState("Pause");


    useEffect(() => {
        if (timerStart === true) {
            const timerID = setInterval(() => {
              setTime(time + 1);
            }, 1000)


            return function () {
              clearInterval(timerID);
            }
        }
    }, [timerStart, time])


    const handleStartButton = () => {
        setTimerStart(true);
    };

    const handlePauseButton = () => {
        setTimerStart(false);
        set
    }


    return (
        <>
            <span>{time}</span>
            <input type='button' value='Start' onClick={handleStartButton} />
            <input type='button' value='Stop' disabled={time > 0 ? false : true} />
            <input type='button' value={buttonValue} hidden={time > 0 ? false : true} onClick={handlePauseButton} />
        </>
    );
}

export default Timer;
