import { useState, useEffect } from 'react';


const Timer = () => {
    const [time, setTime] = useState(0);

    const stat = () => {

        useEffect(() => {
            const timerID = setInterval(() => {
                setTime(time + 1);
            }, 1000)

            return function () {
                clearInterval(timerID);
            }
        }, [time])
    }

    return (
        <p>{time}</p>
    );
}


export default Timer;
