import { useState } from 'react';


const StockManagerSelector = () => {
    const [stockManagerForm, setStockManagerForm] = useState(null);

    return (
        <div>
            <label>I want to </label>

            {/* <select onChange={setManagerForm}> */}
            <select onChange={setManagerForm}>
                <option value={'default'}>Please select</option>
                <option value={'register'}>Register new stock</option>
                <option value={'edit'}>Edit stock</option>
                <option value={'delete'}>Delete stock</option>
            </select>
        </div>
    );
}


const setManagerForm = (e) => {
    if (e.target.value === 'default') setStockManagerForm(null);
    if (e.target.value === 'register') setStockManagerForm(registerForm);
    if (e.target.value === 'edit') setStockManagerForm(editorForm);
    else setStockManagerForm(deleteForm);
}

export {
    StockManagerSelector,
    // setManagerForm
}
