import { useState, useEffect } from 'react';

const Timer = () => {
    const [time, setTime] = useState(0);
    const [timerStart, setTimerStart] = useState(false);
    const [paused, setPaused] = useState(false);


    useEffect(() => {
        if (timerStart === true) {
            const timerID = setInterval(() => {
              setTime(time + 1);
            }, 1000)


            return function () {
              clearInterval(timerID);
            }
        }
    }, [time, timerStart])


    const handleStartBtn = () => {
        setTimerStart(true);
    };



    return (
        <>
            <span>{time}</span>
            <input type='button' value='Start' onClick={handleStartBtn} />
            <input type='button' value='Stop' disabled={timerStart ? false : true} />
            {paused ? <input type='button' value='Resume' hidden={timerStart ? false : true} /> : <input type='button' value='Pause' hidden={true} />}
        </>
    );
}

export default Timer;
