import { useState, useEffect } from 'react';

const Timer = () => {
    const [time, setTime] = useState(0);
    const [timeStatus, setTimeStatus] = useState("Pause");
    const [timePaused, setTimePaused] = useState(true);


    useEffect(() => {
        if (timePaused === false) {
            const timerID = setInterval(() => {
              setTime(time + 1);
            }, 1000)

            return function () {
              clearInterval(timerID);
            }
        }
    }, [time, timePaused])

    const handleStartBtn = () => {
        setTimePaused(false)
    };

    return (
        <>
            <input type='button' value='Start' onClick={handleStartBtn} />
            <span>{time}</span>
            {timePaused ? null : <input type='button' value={timeStatus} onClick={() => {
                setTimePaused(true);
                setTimeStatus("Resume");
            }} />}
            <input type='button' value='Stop' disabled={timePaused ? true : false} />
        </>
    );
}

export default Timer;
