import React, { useState, useEffect } from 'react';
import NavBar from './NavBar';
import Dashboard from './Dashboard';

export default function App() {
  const [stocks, setStocks] = useState([]);
  const [quantityForm, setQuantityForm] = useState({ quantity: null });

  useEffect(() => {
    fetch("http://localhost:3000/materials")
    .then(r => r.json())
    .then(setStocks)
    .catch((err) => alert(err.message))
  }, [])

  const handleQuantityChange = (e) => {
    setQuantityForm({...quantityForm, quantity: +e.target.value})
  };

  const handleQuantitySubmit = (e, targetStock) => {
    e.preventDefault();

    const newStockQuantity = stocks.map((stock) => {
      if (stock.id === targetStock.id) {
        return {
          ...stock, 
          quantity: e.target
        }
      } else {
        return stock;
      }
    })
    setStocks(newStockQuantity);
    console.log(newStockQuantity);
  }

  return (
    <div>
      <h1 className='header'>Welcome to Inventory Manager</h1>

      <NavBar />
      <Dashboard stocks={stocks} handleQuantitySubmit={handleQuantitySubmit} handleQuantityChange={handleQuantityChange} />
    </div>
  );
}
