import React, { useState } from 'react';
// assign stock id when form submit -> Math.floor(1000 + (Math.random() * 9000))
export default function ItemManager() {
    const [formData, setFormData] = useState({
        id: Math.floor(1000 + (Math.random() * 9000)),
        imgURL: "",
        name: "",
        type: "",
        quantity: null,
        supplier: "",
        inStock: true,
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        })
    };


    return (
        <div className='display-component'>
            <h2>Item Manager</h2>

            <form className='item-registrator' onSubmit={null}>
                <h3></h3>
                <input type='text' name='name' onChange={handleChange} placeholder='Item Name'></input>
                <input type='text' name='type' onChange={handleChange} placeholder='Item Type'></input>
                <input type='number' name='quantity' onChange={handleChange} placeholder='Quantity'></input>
                <input type='text' name='supplier' onChange={handleChange} placeholder='Item Supplier'></input>
                <input type='text' name='imgURL' onChange={handleChange} placeholder='Item Image'></input>
                <button>Submit</button>
            </form>
        </div>
    );
};
