const StockPreview = ({ stock }) => {
    return (
        <div>
            {stock ? <div>
                        <img src={stock.imgURL} alt={stock.name} width={'50px'} />
                        <p>{stock.name.charAt(0).toUpperCase() + stock.name.slice(1)}</p>
                        <p>{stock.type.charAt(0).toUpperCase() + stock.type.slice(1)}</p>
                        <p>{stock.quantity}</p>
                        <p>{stock.supplier}</p>
                        <p>{stock.note}</p>
                     </div> : null}
        </div>
    );
}

export default StockPreview;
