import React from 'react';

export default function Dashboard({  // call props from somewhere else (useContext?)
                                    stocks, 
                                    handleFilterByID,
                                    handleFilterByType, 
                                    handleQuantityChange, 
                                    handleQuantitySubmit }) {

    document.title = "Dashboard";

    const types = (stocks.map(stock => stock.type));
    const suppliers = (stocks.map(stock => stock.supplier));

    return (
        <div className="display-component">
            <div className="search-filter-bar">
                <label>Search By: </label>
                <input placeholder='Stock# (4 digits)' maxLength={4} onChange={handleFilterByID} />


                <label> Filter By: </label>
                <select onChange={handleFilterByType}>
                    <option>Type</option>
                    {types.map((type, index) => {
                        if (index === types.indexOf(type)) return <option key={index} name="type" value={type}>{type}</option>
                    })}
                </select>


                <select onChange={null}>
                    <option>Supplier</option>
                    {suppliers.map((supplier, index) => {
                        if (index === suppliers.indexOf(supplier)) return <option key={index} name="supplier" value={supplier}>{supplier}</option>
                    })}
                </select>
            </div>


            <h3>List of Stocks</h3>

            <table>
                <thead className='table-header'>
                    <tr>
                        <td>ID</td>
                        <td>Image</td>
                        <td>Name</td>
                        <td>Type</td>
                        <td>Quantity</td>
                        <td>Supplier</td>
                        <td>In-stock</td>
                    </tr>
                </thead>
                <tbody>
                    {stocks.map(stock => {
                        const { id, imgURL, name, type, quantity, supplier} = stock; 

                        return (
                            <tr key={id} className='table-stocks'>
                                <td>{id}</td>
                                <td><img src={imgURL} alt={name} width={"50px"} onClick={null}/></td>
                                <td>{name}</td>
                                <td>{type}</td>
                                <td>
                                    Current: {quantity}
                                    <form onSubmit={(e) => handleQuantitySubmit(e, stock)}>
                                        <label>New Quantity: </label>
                                        <input type='number' name='quantity' min={0} max={100} onChange={handleQuantityChange} />
                                        <input type='submit' value='Update' />
                                    </form>
                                </td>
                                <td>{supplier}</td>
                                <td>{(stock.quantity > 0) ? "Yes" : "No"}</td>
                            </tr>
                            );
                        })
                    }
                </tbody>
            </table>
        </div>
    )
};
