import { useState, useEffect } from 'react';


const Timer = () => {
    const [time, setTime] = useState(0);

    return (
        <>
            <input type='button' onClick={() => 
                    useEffect(() => {
                        const timerID = setInterval(() => {
                            setTime(time + 1);
                        }, 1000)

                        return function () {
                            clearInterval(timerID);
                        }
                    }, [time])
            } value='Start' />
            <p>{time}</p>
        </>
    );
}


export default Timer;
