import { useState, useEffect } from 'react';

const Timer = () => {
    const [time, setTime] = useState(0);
    const [timePaused, setTimePaused] = useState(true);


    useEffect(() => {
        if (timePaused === false) {
            const timerID = setInterval(() => {
              setTime(time + 1);
            }, 1000)

            return function () {
              clearInterval(timerID);
            }
        }
    }, [time, timePaused])

    return (
        <>
            <input type='button' value='Start' onClick={() => setTimePaused(false)} />
            <span>{time}</span>
            {}

            {/* {timePaused === true ? {time} : 
                                   {time} <input type='button' value='Pause' onClick={() => setTimePaused(true)} />}
            <input type='button' value='Stop' /> */}
        </>
    );
}

export default Timer;
