import { useEffect } from 'react';

import StockRowDetail from './sub_component/StockRowDetail';

const StockDisplayer = ({ filterByID, filteredStocks, setNewQuantity, handleQuantitySubmit }) => {
    useEffect(() => {
        fetch(`http://localhost:3000/materials/${filterByID}`)
        .then(r => r.json())
        .then((stock) => {
            if (stock) return console.log(stock)
            else return console.log('failed');
        })
    })


    return (
        <div className='display-content'>
            <h3>List of Stocks</h3>

            <table>
                <thead className='table-header'>
                    <tr>
                        <td>ID</td>
                        <td>Image</td>
                        <td>Name</td>
                        <td>Type</td>
                        <td>Quantity (max 100)</td>
                        <td>Supplier</td>
                        <td>In-stock</td>
                        <td>Note</td>
                    </tr>
                </thead>

                <tbody>
                    {filteredStocks.map((stock, key) => <StockRowDetail key={key} stock={stock} setNewQuantity={setNewQuantity} handleQuantitySubmit={handleQuantitySubmit} />)}
                </tbody>

            </table>
        </div>
    );
}

export default StockDisplayer;
