import React, { useState } from 'react';

export default function ItemManager() {
    const [formData, setFormData] = useState({
        id: Math.floor(1000 + (Math.random() * 9000)),
        imgURL: "",
        name: "",
        type: "",
        quantity: 0,
        supplier: "",
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        })
    };

    const handleSubmit = () => {
        fetch("http://localhost:3000/materials/", {
            method: "POST",
            headers: {
              "Content-Type": "application/json"
            },
            body: JSON.stringify(formData)
          })
          .then(r => r.json())
          .catch((err) => alert(err.message))
    }

    return (
        <div className='display-component'>
            <h2>Item Manager</h2>

            <form className='item-register' onSubmit={() => handleSubmit()}>
                <h3>Item Register</h3>
                <input type='text' name='name' onChange={handleChange} placeholder='Item Name'></input>
                <input type='text' name='type' onChange={handleChange} placeholder='Item Type'></input>
                <input type='number' name='quantity' min={1} max={100} onChange={handleChange} placeholder='Quantity'></input>
                <input type='text' name='supplier' onChange={handleChange} placeholder='Item Supplier'></input>
                <input type='url' name='imgURL' onChange={handleChange} placeholder='Item Image'></input>
                <button>Submit</button>
            </form>
        </div>
    );
};
