const StockPreview = ({ stock }) => {
    return (
        <div style={{ 'width': '50%', 'margin': '0 auto' }}>
            {stock ? <div>
                        <img src={stock.imgURL} alt={stock.name} width={'80px'} />
                        <p>{stock.name}</p>
                        <p>{stock.type}</p>
                        <p>{stock.quantity}</p>
                        <p>{stock.supplier}</p>
                        <p>{stock.note}</p>
                     </div> : null}
        </div>  
    );
}

export default StockPreview;
