import React, { useState, useEffect } from 'react';
import NavBar from './NavBar';
import Dashboard from './Dashboard';

export default function App() {
  const [stocks, setStocks] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/materials")
    .then(r => r.json())
    .then(setStocks)
    .catch((err) => alert(err.message))
  }, [])

  const handleQuantityChange = (e, targetStockID) => {
    // setStocks({[e.target.name]: e.target.value})
    console.log(targetStockID, [...stocks], {[e.target.name]: +e.target.value});

    const newStockQuantity = stocks.filter((stock) => stock.id === targetStockID)
  };

  const handleQuantitySubmit = (e) => {
    e.preventDefault();

    console.log(e.target);
  }

  return (
    <div>
      <h1 className='header'>Welcome to Inventory Manager</h1>

      <NavBar />
      <Dashboard stocks={stocks} handleQuantitySubmit={handleQuantitySubmit} handleQuantityChange={handleQuantityChange} />
    </div>
  );
}
