import React, { useEffect } from 'react';
import { Routes, Route } from "react-router-dom"
import NavBar from './NavBar';
import Dashboard from './Dashboard';
import StockManager from './StockManager';
import Contact from './Contact';
import ViewStockDetail from './ViewStockDetail';

export default function App() {

  useEffect(() => {
    fetch('http://localhost:3000/materials')
    .then(r => r.json())
    .then(setStocks)
    .catch(err => alert(err.message))
  }, []);

  return (
    <div>
      <h1 className='header'>Welcome to Inventory Manager</h1>

      <NavBar />
      <Routes>
        <Route exact path='/' element={<Dashboard />} />
        <Route exact path='stockmanager' element={<StockManager />} />
        <Route exact path='viewstockdetail/:stockID' element={<ViewStockDetail />} />
        <Route exact path='contact' element={<Contact />} />
      </Routes>
    </div>
  );
}
