import { useEffect } from 'react';


const Timer = ({ time, setTime }) => {
    useEffect(() => {
        const timerID = setInterval(() => {
            setTime(time + 1);
        }, 1000)

        return function () {
            clearInterval(timerID);
        }
    }, [time])

    return <span>{time}</span>;
}


export default Timer;
