import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'

export default function Dashboard() {
  document.title = 'Dashboard';

  const [stocks, setStocks] = useState([]);
  const [newQuantity, setNewQuantity] = useState(0);
  const [filterByID, setFilterByID] = useState(0);
  const [filterByType, setFilterByType] = useState('all');
  const [filterBySupplier, setFilterBySupplier] = useState('all');

  const types = [...stocks].map(stock => stock.type);
  const suppliers = [...stocks].map(stock => stock.supplier);


  // Set fetched stocks to state variable after fetching from the JSON server
  useEffect(() => {
    fetch('http://localhost:3000/materials')
    .then(r => r.json())
    .then(setStocks)
    .catch(err => alert(err.message))
  }, []);


  // Updates quantity of stock from dashboard 
  const handleQuantitySubmit = (e, targetStock) => {
    e.preventDefault();

    const newStocks = [...stocks].map(stock => stock.id === targetStock.id ? {...stock, quantity: newQuantity} : stock); 

    fetch(`http://localhost:3000/materials/${targetStock.id}`, {
        method: "PATCH",
        headers: {'Content-type': 'application/json'},
        body: JSON.stringify({quantity: newQuantity})
    })
    .then(r => r.json())
    .then(setStocks(newStocks))
    .catch(err => alert(err.message))
  };


  const filteredStocks = [...stocks].filter(stock => {if (filterByID === stock.id || filterByID === 0) return true})
                                    .filter(stock => {if (filterByType === stock.type || filterByType === 'all') return true})
                                    .filter(stock => {if (filterBySupplier === stock.supplier || filterBySupplier === 'all') return true})


    return (

        <div className='display-component'>
            <h3>List of Stocks</h3>
            <table>
                <thead className='table-header'>
                    <tr>
                        <td>ID</td>
                        <td>Image</td>
                        <td>Name</td>
                        <td>Type</td>
                        <td>Quantity</td>
                        <td>Supplier</td>
                        <td>In-stock</td>
                        <td>Note</td>
                    </tr>
                </thead>
                <tbody>
                    {filteredStocks.map(stock => {
                        const {id, imgURL, name, type, quantity, supplier, note} = stock; 

                        return (
                            <tr key={id} className='stock-table'>
                                <td>{id}</td>
                                <td>
                                    <Link to={`viewstockdetail/${stock.id}`}>
                                        <img src={imgURL} alt={name} width={'50px'} style={{cursor: 'pointer'}} />
                                    </Link>
                                </td>
                                <td>{name}</td>
                                <td>{type}</td>
                                <td>
                                    Current: <b>{quantity}</b>

                                    <form onSubmit={(e) => handleQuantitySubmit(e, stock)}>
                                        <label>New Quantity: </label>
                                        <input type='number' name='quantity' min={0} max={100} onChange={(e) => setNewQuantity(+e.target.value)} />
                                        <input type='submit' value='Update' />
                                    </form>
                                </td>
                                <td>{supplier}</td>
                                <td>{stock.quantity > 0 ? 'Yes' : 'No'}</td>
                                <td style={{cursor: 'pointer'}} onClick={() => alert('edit note')}>{note}</td>
                            </tr>
                            );
                        })
                    }
                </tbody>
            </table>
        </div>
    )
};
