import { useState, useEffect } from 'react';


const Timer = ({ time, setTime, timePaused, setTimePaused }) => {

    useEffect(() => {
        const timerID = setInterval(() => {
            setTime(time + 1);
        }, 1000)

        return function () {
            clearInterval(timerID);
        }
    }, [time])

    return (
        <>{time}</>
    );
}


export default Timer;
