import React, { useState, useEffect } from 'react';
import { Routes, Route } from "react-router-dom"
import NavBar from './NavBar';
import Dashboard from './Dashboard';
import StockManager from './StockManager';
import Contact from './Contact';
import ViewStockDetail from './ViewStockDetail';

export default function App() {
    const [stocks, setStocks] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3000/materials')
    .then(r => r.json())
    .then(setStocks)
    .catch(err => alert(err.message))
  }, []);

  const handleQuantitySubmit = (e, targetStock) => { // Quantity change should be made in the database (Patch)
    e.preventDefault();

    const newStocks = [...stocks].map(stock => stock.id === targetStock.id ? {...stock, quantity: newQuantity} : stock); 
    setStocks(newStocks);
  };

  return (
    <div>
      <h1 className='header'>Welcome to Inventory Manager</h1>

      <NavBar />
      <Routes>
        <Route exact path='/' element={<Dashboard stocks={stocks} handleQuantitySubmit={handleQuantitySubmit} />} />
        <Route exact path='stockmanager' element={<StockManager />} />
        <Route exact path='viewstockdetail/:stockID' element={<ViewStockDetail stocks={stocks} />} />
        <Route exact path='contact' element={<Contact />} />
      </Routes>
    </div>
  );
}
