import { useEffect } from 'react';


const Timer = ({ time, setTime, timePaused, setTimePaused }) => {
    useEffect(() => {
        const timerID = setInterval(() => {
            setTime(time + 1);
        }, 1000)

        return function () {clearInterval(timerID)};
    }, [time])

    return (
        <>
            {timePaused ? null : <span>{time}</span>}
        </>
    );
}


export default Timer;
