import React from 'react';

export default function Dashboard({ stocks, handleQuantityChange, handleQuantitySubmit }) {
    const handleSearchByID = (e) => {
        const matchingStock = stocks.filter((stock) => stock.id === +e.target.value);
    };

    const handleSearchByName = (e) => {

    }

    return (
        <div className="display-component">
            <div className="search-filter-input">
                <label>Search By: </label>
                <input placeholder='Stock# (4 digits)' maxLength={4} onChange={(e) => handleSearchByID(e)} />
                <input placeholder='Stock Name' maxLength={20} onChange={(e) => handleSearchByName(e)} />
                {/* onChange function should filter as being typed */}

                <label>Filter By: </label>
                <select onChange={null}>
                    <option>Stock Type</option>
                    {stocks.map((stock) => <option key={stock.type} name="type" value={stock.type}>{stock.type}</option>)}
                </select>

                <select onChange={null}>
                    <option>Supplier</option>
                    {stocks.map((stock) => <option key={stock.supplier} name="supplier" value={stock.supplier}>{stock.supplier}</option>)}
                </select>

            </div>

            <h3>List of Stocks</h3>
            <table>
                <thead className='table-header'>
                    <tr>
                        <td>ID</td>
                        <td>Image</td>
                        <td>Name</td>
                        <td>Type</td>
                        <td>Quantity</td>
                        <td>Supplier</td>
                        <td>In-stock</td>
                    </tr>
                </thead>
                <tbody>
                    {stocks.map((stock) => {
                        const { id, imgURL, name, type, quantity, supplier} = stock; 

                        return (
                            <tr key={id} className='table-stocks'>
                                <td>{id}</td>
                                <td>
                                    <img src={imgURL} alt={name} width={"50px"} onClick={() => null}/> 
                                    {/* View Detail Image when image is clicked */}
                                </td>
                                <td>{name}</td>
                                <td>{type}</td>
                                <td>Current: {quantity}
                                    <form onSubmit={(e) => handleQuantitySubmit(e, stock)}>
                                        <label>New Quantity: </label>
                                        <input type='number' name='quantity' min={0} max={100} onChange={(e) => handleQuantityChange(e)} />
                                        <input type='submit' value='Update' />
                                    </form>
                                </td>
                                <td>{supplier}</td>
                                <td>{stock.quantity > 0 ? "Yes" : "No"}</td>
                            </tr>
                            );
                        })
                    }
                </tbody>
            </table>
        </div>
    )
};
