import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

export default function ViewStockDetail() {
    document.title = 'Stock Detail'

    const [stock, setStock] = useState([]);
    const {stockID} = useParams();
    const navigateTo = useNavigate();

    useEffect(() => {
        fetch(`http://localhost:3000/materials/${stockID}`)
        .then(r => r.json())
        .then(setStock)
        .catch(err => alert(err.message))
    }, [])


    const {id, imgURL, name, type, supplier} = stock; 

    return (
        <div className='display-component'>
            <button onClick={() => navigateTo('/')}>Back to Dashboard</button>
            <div className='stock-detail'>
                <p>{name} ({type})</p>
                <img src={imgURL} alt={name} width={'200px'}/>
                <p>Stock# {id}</p>
                <p>Supplier: {supplier}</p>

            </div>
        </div>
    );
}
