import { useNavigate } from 'react-router-dom';
import EditorForm from './EditorForm';
import DeleteForm from './DeleteForm';

const StockManagerSelector = () => {
    const navigateTo = useNavigate();

    const setManagerForm = (e) => {
        if (e.target.value === 'default') navigateTo('registernewstock');
        if (e.target.value === 'register') setStockManagerForm(EditorForm);
        if (e.target.value === 'edit') setStockManagerForm(DeleteForm);
        else setStockManagerForm(null);
    }

    return (
        <div>

            <label>I want to </label>
            <select onChange={setManagerForm}>
                <option value={'default'}>Please select</option>
                <option value={'register'}>Register new stock</option>
                <option value={'edit'}>Edit stock</option>
                <option value={'delete'}>Delete stock</option>
            </select>

            {stockManagerForm ? stockManagerForm : null}
        </div>
    );
}

export default StockManagerSelector;
