import { useState, useEffect } from 'react';

const Timer = () => {
    const [time, setTime] = useState(0);
    const [timeStatus, setTimeStatus] = useState("Pause");
    const [showBtn, setShowBtn] = useState("hide");

    useEffect(() => {
        if (timeStatus !== "Pause") {
            const timerID = setInterval(() => {
              setTime(time + 1);
            }, 1000)


            return function () {
              clearInterval(timerID);
            }
        }
    }, [time, timeStatus])


    const handleStartBtn = () => {
        setTimeStatus("Resume");
        setShowBtn("show")
    };


    const handlePauseResumeBtn = () => {
        setTimeStatus("Pause");
        // setShowBtn("hide");
    };


    return (
        <>
            <span>{time}</span>
            <input type='button' value='Start' onClick={handleStartBtn} />
            <input type='button' value='Stop' disabled={timeStatus === "Pause" ? true : false} />
            <input type='button' value={timeStatus === "Pause" ? "Resume" : "Pause"} hidden={showBtn === "hide" ? true : false} onClick={handlePauseResumeBtn}/>
        </>
    );
}

export default Timer;
