import { useState, useEffect } from 'react';

const [stocks, setStocks] = useState([]);
const useFetch = (url) => {

    useEffect(() => {
        fetch(url)
        .then(r => r.json())
        .then(setStocks)
        .catch(err => alert(err.message))
    }, [url])

    return [stocks];
}

export default useFetch;
