import { useState, useEffect } from 'react';

const Timer = () => {
    const [time, setTime] = useState(0);
    const [timePaused, setTimePaused] = useState(true);

    const handleStartBtn = () => setTimePaused(false);


    const handlePauseBtn = () => {
        setTimePaused(true);
    }


    useEffect(() => {
        if (timePaused === false) {
            const timerID = setInterval(() => {
              setTime(time + 1);
            }, 1000)

            return function () {
              clearInterval(timerID);
            }
        }
    }, [time, timePaused])

    return (
        <>
            <input type='button' value='Start' onClick={() => setTimePaused(false)} />
            <span>{time}</span>
            {timePaused ? {time} : <input type='button' value='Pause' onClick={() => setTimePaused(false)} />}
            <input type='button' value='Stop' disabled={timePaused ? true : false} />
        </>
    );
}

export default Timer;
